class E<X> {} interface I1 { func f1(): Unit
}
interface I2 { func f2(): Unit
} extend<X> E<X> <: I1 where X <: B { // extension 1 public func f1(): Unit { f2() // OK }
} extend<X> E<X> <: I2 where X <: A { // extension 2 public func f2(): Unit { f1() // Error }
