#include<stdint.h> typedef struct { int64_t x; int64_t y;
} Point; typedef struct { float x; float y; float z;
} Cube; int32_t drawPicture(Point* point, Cube* cube) { point->x = 1; point->y = 2; printf("Draw Point finished.\n"); printf("Before draw cube\n"); printf("%f\n", cube->x); printf("%f\n", cube->y); printf("%f\n", cube->z); cube->x = 4.4; cube->y = 5.5; cube->z = 6.6; printf("Draw Cube finished.\n");
} 仓颉代码如下： // main.cj
